import React, { useState, useEffect, useCallback } from 'react';
import { ExportCSV } from './ExportCSV ';
import "./css/buttons_styles.css"
import ReactFlow, {
    removeElements,
    updateEdge,
    addEdge,
    Background,
    Controls,
    ReactFlowProvider,
    useZoomPanHelper
} from 'react-flow-renderer';
import localforage from 'localforage';
import { nodeTypes } from './Nodes';
// const socket = new WebSocket('ws://localhost:5000/')
localforage.config({
    name: 'react-flow-docs',
    storeName: 'flows',
});



 class LoadingJson extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null
    };
    this.handleFileSelect = this.handleFileSelect.bind(this);
  }

  displayData(content) {
    this.setState({ data: content });
  }

  handleFileSelect(evt) {
    let files = evt.target.files;
    if (!files.length) {
      alert('Файл не выбран');
      return;
    }
    let file = files[0];
    let that = this;
    let reader = new FileReader();
    reader.onload = function (e) {
      that.displayData(e.target.result);
    };
    reader.readAsText(file);
  }




  render() {

    const ReactFlowRenderer = (props) => {

      const [elements, setElements] = useState([]);

    // ------------------- Экспорт в Excel ------------------

    const fileName = 'Scada'
    const [loadingJson, setloadinJson] = useState("");
    const [name, setName] = useState("");
    const [activeNode, setActiveNode] = useState();
    const [newName, setNewName] = useState("");
    const [instance, setInstance] = useState();
    const { transform } = useZoomPanHelper();

    const flowKey = 'example-flow';

    useEffect(() => {
        if (activeNode) setNewName(activeNode.data.label);
    }, [activeNode]);


    // --------------- Сохранение в локальном хранилище ---------------

    const onSave = useCallback(() => {

        if (loadingJson) {
            const flow = loadingJson.toObject();
            localforage.setItem(flowKey, flow);
            console.log("Объекты сохранены")
        }

    }, [instance]);



    // --------------- Загрузка ---------------

    const onRestore = useCallback(() => {
        const restoreFlow = async () => {
            const flow = await localforage.getItem(flowKey);


            if (flow) {
                const [x = 0, y = 0] = flow.position;
                setElements(flow.elements || []);
                transform({ x, y, zoom: flow.zoom || 0 });
            }

        };
        restoreFlow();
    }, [setElements, transform]

    );


    // --------------- Загрузка через локальный диск ---------------

    const onLoadingJson = useCallback(() => {
        const restoreFlow = async () => {
            const flow = await JSON.parse(loadingJson);


            if (flow) {
                const [x = 0, y = 0] = flow.position;
                setElements(flow.elements || []);
                transform({ x, y, zoom: flow.zoom || 0 });
            }

        };
        restoreFlow();
    }, [setElements, transform]

    );

    // --------------- Сохранение через локальный диск ---------------


    const exportToJson = () => {
        let filename = "Scada-systems.json";
        let contentType = "application/json;charset=utf-8;";
        if (window.navigator && window.navigator.msSaveOrOpenBlob) {
            const blob = new Blob([decodeURIComponent(encodeURI(JSON.stringify(elements)))], { type: contentType });
            navigator.msSaveOrOpenBlob(blob, filename);
        } else {
            const a = document.createElement('a');
            a.download = filename;
            a.href = 'data:' + contentType + ',' + encodeURIComponent(JSON.stringify(elements, null, 1));
            a.target = '_blank';
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
        }
    }


    // --------------- Удаление ---------------


    const elementRemoveHandler = (elementTobeRemoved) => {
        setElements((prev) => removeElements(elementTobeRemoved, prev));
    };


    const connectHandler = (params) => {
        setElements((els) =>
            addEdge({ ...params, type: 'step', style: { stroke: 'red' } }, els))
    };




    // ----------------------- Добавление элементов --------------------------

    const addRectangleHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "pump",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addCircleHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "circle",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addFlagHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "flag",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    const addControlUnit = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "controlunit",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    // ------------------- Добавление независимой задвижки ----------------

    const addIndGateValveHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "indgatevalve",
            position: {
                x: -10,
                y: 30
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    // ------------------- Добавление задвижки ----------------

    const addGateValveHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "gatevalve",
            position: {
                x: -10,
                y: 30
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    const addGateValveHandlerRight = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "gatevalveright",
            position: {
                x: -10,
                y: 30
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    const addGateValveHandlerBot = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "gatevalvebot",
            position: {
                x: -10,
                y: 30
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    const addGateValveHandlerLeft = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "gatevalveleft",
            position: {
                x: -10,
                y: 30
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    // -----------------------------------

    const addTextHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "text",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };




    // --------------- Убрать, грубо говоря, линии с задвижки ---------------


    const edgeUpdateHandler = (oldEdge, newConnection) =>
        setElements((els) => updateEdge(oldEdge, newConnection, els));



    // --------------- Приближении при двойном нажатии на мышь ---------------

    const clickHandler = (e) => {
        var htmlString = e.target.outerHTML.toString();
        var index = htmlString.indexOf(` id="`);
        index += 15;
        const currentId = htmlString.substr(index, 13);

        elements.forEach((_current) => {
            if (_current.id === currentId) {
                setActiveNode(_current);
            }
        });
        // setNewName(activeNode.data.label)
    };


    // --------------- Обновление текста ---------------

    const updateNodeHandler = () => {
        if (!activeNode) return;
        setElements(
            elements.map((_current) => {
                if (_current.id === activeNode.id) {
                    return {
                        ..._current,
                        data: { label: newName, id: _current.data.id }
                    };
                }

                return _current;
            })
        );
    };

    const onLoad = (reactFlowInstance) => {
        setInstance(reactFlowInstance);
        reactFlowInstance.fitView();
    };




    // --------------- Сохранение через сервер ---------------


    // //save pos
    // const saveChangesHandler = () => {
    //     console.log("state", instance.getElements());
    //     //отправка json

    //     localStorage.setItem('state', JSON.stringify(instance.getElements()));
    //     var pos = JSON.stringify(instance.getElements());
    //     socket.send(pos);
    // };

    // socket.onopen = () => {
    //     console.log('подключено');
    // }

    // socket.onmessage = (event) => {
    //     console.log('есть сообщение')
    //     const dataSave = JSON.parse(JSON.stringify(event.data));
    //     console.log(dataSave)
    // }



    }
    const data = this.state.data;
    return (
      <div >
        <input type="file" onChange={this.handleFileSelect} />
      </div>
    );
  }
}


export default LoadingJson ;


