import React, { useState, useEffect, useCallback } from 'react';
import { ExportCSV } from './ExportCSV ';
import "./css/buttons_styles.css"
import ReactFlow, {
    removeElements,
    updateEdge,
    addEdge,
    Background,
    useNodesState,
    useEdgesState,
    Controls
} from 'react-flow-renderer';
import { nodeTypes } from './Nodes';
import styleConnect from './style-connect';
const socket = new WebSocket('ws://localhost:5000/')

const ReactFlowRenderer = () => {
    const [elements, setElements] = useState([styleConnect]);

    // ------------------- Экспорт в Excel ------------------

    const fileName = 'Scada'
    const [name, setName] = useState("");
    const [activeNode, setActiveNode] = useState();
    const [newName, setNewName] = useState("");
    const [instance, setInstance] = useState();
    const [connected, setConnected] = useState(false);
    const [nodes, setNodes, onNodesChange] = useNodesState(elements);
    const [edges, setEdges, onEdgesChange] = useEdgesState(elements);


    useEffect(() => {
        if (activeNode) setNewName(activeNode.data.label);
    }, [activeNode]);


    // --------------- Удаление ---------------


    const elementRemoveHandler = (elementTobeRemoved) => {
        setElements((prev) => removeElements(elementTobeRemoved, prev));
    };


    const flowKey = 'example-flow';

    const connectHandler = (params) => {
        setElements((els) =>
            addEdge({ ...params, type: 'step', style: { stroke: 'red' } }, els))
    };

    const onSave = useCallback(() => {

        if (instance) {
            const flow = instance.toObject();
            localStorage.setItem(flow, JSON.stringify(flow));
        }
    }, [instance]);


    const onRestore = useCallback(() => {
        const restoreFlow = async () => {
          const flow = JSON.parse(localStorage.getItem(flowKey));

          if (flow) {
            const { x = 0, y = 0, zoom = 1 } = flow.viewport;
            setElements(flow.elements || []);
            setViewport({ x, y, zoom });
          }
        };

        restoreFlow();
      }, [setElements]);



    // ----------------------- Добавление элементов --------------------------

    const addRectangleHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "rectangle",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addCircleHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "circle",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addFlagHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "flag",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    const addControlUnit = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "controlunit",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
