import React from "react";
import { Handle } from 'react-flow-renderer';



//  ---------------------  Квадрат  -----------------------

const RectangleNode = ({ data }) => {
  return (
    <div style={{ background: "#9ca8b3", padding: "14px" }}>
      <Handle
        type="target"
        position="left"
        id={`${data.id}.left`}
        style={{ borderRadius: 0 }}
      />
      <div id={data.id}>{data.label}</div>
      <Handle
        type="source"
        position="right"
        id={`${data.id}.right`}
        style={{ borderRadius: 0 }}
      />
    </div>
  );
};

//  ---------------------  Регулятор  -----------------------

const CircleNode = ({ data }) => {
  return (
    <div
      style={{
        backgroundColor: "green",
        width: '100px',
        height: '100px',
        borderRadius: "100px",
      }}
    >
      <Handle
        type="target"
        position="left"
        id={`${data.id}.left`}
        style={{ borderRadius: "0" }}
      />
      <div id={data.id}>{data.label}</div>
      <Handle
        type="source"
        position="right"
        id={`${data.id}.right`}
        style={{ borderRadius: 0 }}
      />


      <div className="rect-node-circle">
        <div id={data.id}>{data.label}</div>
      </div>


      <div className="rect-node-circle-center">
        <div id={data.id}>{data.label}</div>
      </div>
    </div>
  );
};


//  ---------------------  Задвижка  -----------------------


const GateValve = ({ data }) => {
  return (
    <div>
      <div className="triangle-node-right">
        <div id={data.id} className="triangle-node-text">
          {data.label}
          <Handle
            type="target"
            position="top"
            id={`${data.id}.top`}
            style={{ borderRadius: 0, width: '10px', height: '10px' }}
          />
        </div>
      </div>


      <div className="triangle-node-left">
        <div id={data.id} className="triangle-node-text">
          {data.label}
        </div>
        <Handle
          type="source"
          position="top"
          id={`${data.id}.top`}
          style={{ top: '35px', borderRadius: 0, width: '10px', height: '10px' }}
        />
      </div>



      <div className="rect-node-top">
        <div id={data.id}>{data.label}</div>
      </div>
      <div className="rect-node-center">
        <div id={data.id}>{data.label}</div>
      </div>
    </div>

  );
};

//  ---------------------  Флажок  -----------------------

const Flag = ({ data }) => {
  return (
    <div>
      <div className="Flag">
        <div id={data.id} className="triangle-node-text">
          {data.label}
        </div>
      </div>

      <div className="flag-node-center">
        <div id={data.id}>{data.label}</div>
        <Handle
          type="source"
          position="left"
          id={`${data.id}.top`}
          style={{ top: '35px', borderRadius: 0, width: '10px', height: '10px',top: '108px' }}
        />
      </div>
    </div>
  );
};


//  ---------------------  Text  -----------------------

export const TextNode = ({ data }) => {
  return (
    <div style={{ background: "transparent", padding: "14px" }}>
      <div id={data.id}>{data.label}</div>
    </div>
  );
};

export const nodeTypes = {
  circle: CircleNode,
  rectangle: RectangleNode,
  gatevalve: GateValve,
  flag: Flag,
  text: TextNode
};
