import React from "react";
import { Handle } from 'react-flow-renderer';
import { Tooltip } from '@mui/material';
import "./elements styles.css";


//  ---------------------  Цистерна  -----------------------

const RectangleNode = ({ data }) => {
  return (
    <Tooltip title="Цистерна">
      <div style={{ background: "#9ca8b3", width: "150px", height: "75px", borderRadius: "100px/50px", borderStyle: "solid" }}>
        {/* <Handle
        type="target"
        position="left"
        id={`${data.id}.left`}
        style={{ borderRadius: 0 }}
      />
      <div id={data.id}>{data.label}</div>
      <Handle
        type="source"
        position="right"
        id={`${data.id}.right`}
        style={{ borderRadius: 0 }}
      /> */}

        <div className="cistern">
          <div id={data.id}>{data.label}</div>
        </div>
      </div>
    </Tooltip>
  );
};

//  ---------------------  Регулятор  -----------------------

const CircleNode = ({ data }) => {
  return (
    <Tooltip title="Регулятор">
      <div
        style={{
          backgroundColor: "green",
          width: '100px',
          height: '100px',
          borderRadius: "100px",
        }}
      >
        <Handle
          type="target"
          position="left"
          id={`${data.id}.left`}
          style={{ borderRadius: "0" }}
        />
        <div id={data.id}>{data.label}</div>
        <Handle
          type="source"
          position="right"
          id={`${data.id}.right`}
          style={{ borderRadius: 0 }}
        />


        <div className="rect-node-circle">
          <div id={data.id}>{data.label}</div>
        </div>


        <div className="rect-node-circle-center">
          <div id={data.id}>{data.label}</div>
        </div>
      </div>
    </Tooltip>
  );
};


//  ---------------------  Задвижка  -----------------------


const GateValve = ({ data }) => {
  return (
    <Tooltip title="Задвижка">
      <div>
        <div className="triangle-node-right">

          <div id={data.id} className="triangle-node-text">
            {data.label}
            <Handle
              type="target"
              position="top"
              id={`${data.id}.top`}
              style={{ borderRadius: 0, width: '10px', height: '10px' }}
            />
          </div>
        </div>


        <div className="triangle-node-left">
          <div id={data.id} className="triangle-node-text">
            {data.label}
          </div>
          <Handle
            type="source"
            position="top"
            id={`${data.id}.top`}
            style={{ top: '35px', borderRadius: 0, width: '10px', height: '10px' }}
          />
        </div>



        <div className="rect-node-top">
          <div id={data.id}>{data.label}</div>
        </div>
        <div className="rect-node-center">
          <div id={data.id}>{data.label}</div>
        </div>
      </div>
    </Tooltip>
  );
};

//  ---------------------  Задвижка Правая -----------------------


const GateValveRight = ({ data }) => {
  return (
    <Tooltip title="Задвижка">
      <div className="GateValveRight">
        <div className="triangle-node-right">

          <div id={data.id} className="triangle-node-text">
            {data.label}
            <Handle
              type="target"
              position="top"
              id={`${data.id}.top`}
              style={{ borderRadius: 0, width: '10px', height: '10px' }}
            />
          </div>
        </div>


        <div className="triangle-node-left">
          <div id={data.id} className="triangle-node-text">
            {data.label}
          </div>
          <Handle
            type="source"
            position="bottom"
            id={`${data.id}.left`}
            style={{ top: '35px', borderRadius: 0, width: '10px', height: '10px' }}
          />
        </div>



        <div className="rect-node-top">
          <div id={data.id}>{data.label}</div>
        </div>
        <div className="rect-node-center">
          <div id={data.id}>{data.label}</div>
        </div>
      </div>
    </Tooltip>
  );
};

//  ---------------------  Флажок  -----------------------

const Flag = ({ data }) => {
  return (
    <Tooltip title="Флаг">
      <div>
        <div className="Flag">
          <div id={data.id} className="triangle-node-text">
            {data.label}
          </div>
        </div>

        <div className="flag-node-center">
          <div id={data.id}>{data.label}</div>
          <Handle
            type="source"
            position="left"
            id={`${data.id}.top`}
            style={{ top: '115px', left: '3px', borderRadius: 0, width: '10px', height: '10px' }}
          />
        </div>
        <div className="flag-node-top">
          <div id={data.id}>{data.label}</div>
          <Handle
            type="target"
            position="left"
            id={`${data.id}.top`}
            style={{ top: '105px', left: '3px', borderRadius: 0, width: '10px', height: '10px' }}
          />
        </div>
      </div>
    </Tooltip>
  );
};


//  ---------------------  Text  -----------------------

export const TextNode = ({ data }) => {
  return (
    <div style={{ background: "transparent", padding: "14px" }}>
      <div id={data.id}>{data.label}</div>
    </div>
  );
};

export const nodeTypes = {
  circle: CircleNode,
  rectangle: RectangleNode,
  gatevalve: GateValve,
  gatevalveright: GateValveRight,
  flag: Flag,
  text: TextNode
};
