import React, { useState, useEffect } from 'react';
import { Tooltip } from '@mui/material';
import "./styles.css";
import ReactFlow, {
    removeElements,
    updateEdge,
    addEdge,
    Background,
    // MiniMap,
    Controls
} from 'react-flow-renderer';

import { nodeTypes } from './Nodes';
import styleConnect from './style-connect';
const socket = new WebSocket('ws://localhost:5000/')

const ReactFlowRenderer = () => {
    const [elements, setElements] = useState([styleConnect]);

    const [name, setName] = useState("");
    const [activeNode, setActiveNode] = useState();
    const [newName, setNewName] = useState("");
    const [instance, setInstance] = useState();

    useEffect(() => {
        if (activeNode) setNewName(activeNode.data.label);
    }, [activeNode]);

    const elementRemoveHandler = (elementTobeRemoved) => {
        setElements((prev) => removeElements(elementTobeRemoved, prev));
    };

    const connectHandler = (params) => {
        setElements((els) =>
            addEdge({ ...params, type: 'smoothstep', style: { stroke: '#f00' }, animated: true }, els))
    };

    const addRectangleHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "rectangle",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addCircleHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "circle",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addFlagHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "flag",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };


    const addGateValveHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "gatevalve",
            position: {
                x: -10,
                y: 30
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const addTextHandler = () => {
        const newNode = {
            id: `${Date.now()}`,
            data: { label: `${name}` },
            type: "text",
            position: {
                x: 0,
                y: 0
            }
        };
        newNode.data = { ...newNode.data, id: `${newNode.id}` };

        setElements((prev) => {
            return [...prev, newNode];
        });
        setName("");
    };

    const edgeUpdateHandler = (oldEdge, newConnection) =>
        setElements((els) => updateEdge(oldEdge, newConnection, els));

    const clickHandler = (e) => {
        var htmlString = e.target.outerHTML.toString();
        var index = htmlString.indexOf(` id="`);
        index += 5;
        const currentId = htmlString.substr(index, 13);

        elements.forEach((_current) => {
            if (_current.id === currentId) {
                setActiveNode(_current);
            }
        });
        // setNewName(activeNode.data.label)
    };


    // --------------- Удаление ---------------


    const deleteNode = (id) => {
        if (elements[id] != undefined) {
            setElements((els) => removeElements([elements[id]], els));
        }
        else {
            elements[id] = function (event) {
                console.log(event);
            }
        }
    };



    const updateNodeHandler = () => {
        if (!activeNode) return;
        setElements(
            elements.map((_current) => {
                if (_current.id === activeNode.id) {
                    return {
                        ..._current,
                        data: { label: newName, id: _current.data.id }
                    };
                }

                return _current;
            })
        );
    };


    const onLoad = (reactFlowInstance) => {
        setInstance(reactFlowInstance);
        reactFlowInstance.fitView();
    };

    //save pos
    const saveChangesHandler = () => {
        console.log("state", instance.getElements());
        //отправка json
        localStorage.setItem('state', JSON.stringify(instance.getElements()));
        var pos = JSON.stringify(instance.getElements());
        socket.send(pos);
    };

    socket.onopen = () => {
        console.log('подключено');
    }

    socket.onmessage = (event) => {
        console.log('есть сообщение', event.data)
    }

    return (
        <div
            style={{
                height: "75vh",
                width: "75vw",
                border: "1px solid black",
                marginLeft: "12.5vw"
            }}
        >
            <ReactFlow
                elements={elements}
                onElementsRemove={elementRemoveHandler}
                onConnect={connectHandler}
                deleteKeyCode={8 || 46}
                onEdgeUpdate={edgeUpdateHandler}
                nodeTypes={nodeTypes}
                snapToGrid={true}
                snapGrid={[16, 16]}
                connectionLineStyle={{ stroke: "red", strokeWidth: 2 }}
                onDoubleClick={clickHandler}
                onLoad={onLoad}
            >
                <Background variant="dots" gap={15} size={2} color="#c8c8c8" />
                {/* Мини карта
            <MiniMap
                nodeColor={(node) => {
                    switch (node.type) {
                        case "rectangle":
                            return "red";
                        case "circle":
                            return "#00ff00";
                        case "triangle":
                            return "rgb(0,0,255)";
                        default:
                            return "#eee";
                    }   
                }}
            /> */}

                <Controls />
            </ReactFlow>

            <div>
                <input
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    type="text"
                    placeholder="Введите текст"
                />

                <Tooltip title="Кнопка цистерны">
                    <button type="button" onClick={addRectangleHandler}>
                        Цистерна
                    </button>
                </Tooltip>


                <button type="button" onClick={addCircleHandler}>
                    Регулятор
                </button>

                <button type="button" onClick={addGateValveHandler}>
                    <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/0/00/Disk_pack1.svg/1200px-Disk_pack1.svg.png"></img>
                    Задвижка
                </button>

                <button type="button" onClick={addFlagHandler}>
                    Флаг
                </button>

                <button type="button" onClick={addTextHandler}>
                    Текст
                </button>
                <button type='button' onClick={() => deleteNode(0)}>
                    Удаление
                </button>
            </div>

            <div>
                <input
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                    type="text"
                />

                <button type="button" onClick={updateNodeHandler}>
                    Обновить
                </button>
            </div>
            <div>
                <button type="button" onClick={saveChangesHandler}>
                    Сохранить
                </button>
                {/*<button type='button' onClick={sendPos}>
                Send Pos on Server
            </button>*/}
            </div>

        </div>
    );
};

export default ReactFlowRenderer;
